mapper.select:
SELECT
FROM sys_user user JOIN sys_user_role user_role ON (user.id = user_role.user_id) JOIN sys_role role ON (user_role.role_id = role.id) LEFT JOIN sys_role_resc role_resc ON (role_resc.role_id = role.id) LEFT JOIN sys_resc resc ON (resc.id = role_resc.resc_id) WHERE user.username = #{username,jdbcType=VARCHAR} UNION SELECT
FROM sys_user user JOIN sys_user_group user_group ON (user.id = user_group.user_id) JOIN sys_group grp ON (grp.id = user_group.group_id) JOIN sys_group_role group_role ON (group_role.group_id = grp.id) JOIN sys_role role ON (role.id = group_role.role_id) LEFT JOIN sys_role_resc role_resc ON (role_resc.role_id = role.id) LEFT JOIN sys_resc resc ON (resc.id = role_resc.resc_id) WHERE user.username = #{username,jdbcType=VARCHAR}
